import { createStore} from "https://cdn.skypack.dev/redux@4.0.5";

const initialState = {
  userLoginInfo:{
    email: "",
    password: "",
  },
  userInfo:{
    firstName: "",
    lastName: "",
    id: "",
    editNameButton: false,
    saveNewNameButton: false,
  },
  loading: true,
  connected: false,
  token:"",
};

export const setToken = (value) => ({ 
  type: "setToken",
  payload: value,
});

export const setUserInfo = (value) => ({ 
  type: "setUserInfo",
  payload: value,
});

export const editNameButton = (value) => ({ 
  type: "editNameButton",
  payload: value,
});

export const saveNewNameButton = (value) => ({ 
  type: "saveNewNameButton",
  payload: value,
});

// le reducer est une fonction
export function reducer(state = initialState, action) {

  if (action.type === 'setToken') {
      return{
        ...state,
        loading: false,
        connected: true, 
        token: action.payload,
    }
  }
  if (action.type === 'setUserInfo') {
    console.log(action.payload)
    return{
      ...state,
      userLoginInfo:{
        email: action.payload.email,
        password: action.payload.password,
      },
      userInfo:{
        firstName: action.payload.firstName,
        lastName: action.payload.lastName,
        id: action.payload.id,
      }, 
    }
  }
  if (action.type === 'editNameButton') {
    console.log(action.payload)
    return{
      ...state,
      userInfo:{
        ...state,
        editNameButton: action.payload,
      },
    }
  }
  if (action.type === 'saveNewNameButton') {
    console.log(action.payload)
    return{
      ...state,
      userInfo:{
        ...state,
        saveNewNameButton: action.payload,
      },
  }

}
  // if (action.type === "userChange") {
  //   return{
  //     ...state,
  //     userInfo:{
  //       firstName: action.payload.firstName,
  //       lastName: action.payload.lastName,
  //     },
  //   }
  // }
  // Otherwise we return the state without changing it
  return state;
}

// We create the store with the state and the reducer
export const store = createStore(reducer);
export const state = store.getState();
