import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import { FiLogOut } from 'react-icons/fi';
import { FaUserCircle } from 'react-icons/fa';

import "../styles/main.css"

import Logo from "../assets/argentBankLogo.png";

export function Navbar() {

  const userFirstName = useSelector((state) => state.userInfo.firstName);


  return (
    <div>
        <nav className="main-nav">
          <Link to="/" className="main-nav-logo">
            <img className="main-nav-logo-image" src={Logo} alt="Argent Bank Logo"/>
            <h1 className="sr-only">Argent Bank</h1>
          </Link>
          {userConnected ? (
            <div className="username">
              <p><i className="fa-solid fa-user"></i>{userFirstName}</p>
              <Link to="/"><FaUserCircle/><FiLogOut/>Logout</Link>
            </div>
          ) : (
            <Link to="/login"><i className="fa fa-user-circle"></i>Sign In</Link>
          )}

        </nav>

    </div>
  );
}
