import { useSelector } from "react-redux";
import { store, setUserInfo, editNameButton } from "../redux/store";

import { EditForm } from "./EditForm";
import "../styles/main.css";

export function ButtonEditName() {
  const user = useSelector((state) => state.userInfo);
  const token = useSelector((state) => state.token);
  const clicSaveButton = useSelector((state) => state.userInfo.saveNewNameButton);
  const clicEditButton = useSelector((state) => state.userInfo.editNameButton);

  console.log(clicEditButton)
  //Send request if user data is empty
  if(!user.firstName){
    const url = "http://localhost:3001/api/v1/user/profile";

    // Send POST request:
    fetch(url, {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
    })
      .then(function(res) {
        return res.json()
        .then(function(value) {
          console.log(value)
          //Send user data to state
          store.dispatch(setUserInfo(value.body));

        })
        .catch(function(err) {
          console.log(err)
      });

      })
  }

  const handleClick = () => {
    // click on editname button
    store.dispatch(editNameButton(true))
      let button = document.querySelector(".show-button-edit");
      button.style.display = 'none';    

  }

    return (
     <div>
      {!clicEditButton ? (
        <EditForm />
       ) : (
        <div className="show-button-edit">
          <h1>Welcome back<br />{user.firstName} {user.lastName}</h1>
          <button className="edit-button" onClick={handleClick}>Edit Name</button>
        </div> 
       )}
     </div>
    )
    ;
}
