import { useSelector } from "react-redux";
import { store, userIsConnected } from "../redux/store";

import { EditForm } from "../components/EditForm"
import "../styles/main.css"

export function User() {
  const user = useSelector((state) => state.userInfo);
  const token = useSelector((state) => state.token);

  //Send request if user data is empty
  if(!user.firstName){
    const url = "http://localhost:3001/api/v1/user/profile";

    // Send POST request:
    fetch(url, {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
    })
      .then(function(res) {
        return res.json()
        .then(function(value) {
          console.log(value)
          //Send user data to state
          store.dispatch(setUserInfo(value.body))  
        })
        .catch(function(err) {
          console.log(err)
      });

      })
  }
  const handleClick = () =>{
    let form = document.querySelector(".show-edit");
    let title = document.querySelector(".show-title-edit");
    let button = document.querySelector(".show-button-edit");

    form.style.display = 'block';
    title.style.display = 'none';
    button.style.display = 'none';
  }

    return (
        <div>
            <main className="main bg-dark">
                <div className="header">
                    <h1 className="show-title-edit">Welcome back<br />{user.firstName} {user.lastName}</h1>
                    <button className="show-button-edit edit-button" onClick={handleClick}>Edit Name</button>
                    <div className="show-edit">
                      <EditForm className="EditForm" firstName={user.firstName} lastName={user.lastName}/>
                    </div>
                </div>
                <h2 className="sr-only">Accounts</h2>
                <section className="account">
                    <div className="account-content-wrapper">
                    <h3 className="account-title">Argent Bank Checking (x8349)</h3>
                    <p className="account-amount">$2,082.79</p>
                    <p className="account-amount-description">Available Balance</p>
                    </div>
                    <div className="account-content-wrapper cta">
                    <button className="transaction-button">View transactions</button>
                    </div>
                </section>
                <section className="account">
                    <div className="account-content-wrapper">
                    <h3 className="account-title">Argent Bank Savings (x6712)</h3>
                    <p className="account-amount">$10,928.42</p>
                    <p className="account-amount-description">Available Balance</p>
                    </div>
                    <div className="account-content-wrapper cta">
                    <button className="transaction-button">View transactions</button>
                    </div>
                </section>
                <section className="account">
                    <div className="account-content-wrapper">
                    <h3 className="account-title">Argent Bank Credit Card (x8349)</h3>
                    <p className="account-amount">$184.30</p>
                    <p className="account-amount-description">Current Balance</p>
                    </div>
                    <div className="account-content-wrapper cta">
                    <button className="transaction-button">View transactions</button>
                    </div>
                </section>
            </main>
        </div>

    );
}
